import { useState, useEffect } from 'react'
import axios from 'axios'
import './Lodging.css'

const App = () => {
  const [lodgings, setLodgings] = useState([])
  const [departure, setDeparture] = useState(new Date())
  
  const changeLodging = (event) => {
    event.preventDefault()
  }

  const handleDepartureChange = (event) => {
    setDeparture(new Date(event.target.value))
  }

  useEffect(() => {
    console.log('effect')
    axios
      .get('http://localhost:3001/api/lodgings')
      .then(response => {
        console.log('promise fulfilled')
        setLodgings(response.data)
      })
  }, [])

  return (
    <div id='wrapper'>
      <div id='header'>
        <h1>Le Motel de Puro</h1>
      </div>
      <div id='middle'>
        <div className='formBG'>
          <h2>LOPETA MAJOITTAUTUMINEN</h2>
          <form onSubmit={changeLodging}>
            <p>Saapumispäivä: </p>
            <p>Henkilöiden määrä: </p>
            <label>Lähtöpäivä:</label> <br/>
              <input id='date' value={departure.toISOString().slice(0, 10)} type='date' onChange={handleDepartureChange}/> <br/>
          </form>
        </div>
      </div>
      <div id='footer'>
        <p>Heta Puro 2022</p>
      </div>
    </div>
  )

}

export default App
