const App = () => {
  const [lodgings, setLodgings] = useState([
    { name: 'Arto Hellas' }
  ]) 
  const [newName, setNewName] = useState('')
  const [adults, setAdults] = useState('')
  const [children, setChildren] = useState('')

  function minusA() {
    adults =- 1
    setAdults(adults)
  }

  function plusA() {
    adults =+ 1
    setAdults(adults)
  }

  return (
    <div>
      <h2>Majoittaudu</h2>
      <form>
        <div>
          <label for='arrival'>Saapumispäivä:</label> 
          <br/>
          <input name='arrival' type='date'/>
          <br/>
        </div>
        <div>
          <button id='minusA'>-</button>
          <span id='adults'>{adults}</span>
          <button id='plusA'>+</button>
        </div>
        <div>
          <button id='minusC'>-</button>
          <span id='children'>{children}</span>
          <button id='plusC'>+</button>
        </div>
      </form>
      <h2>Numbers</h2>
      ...
    </div>
  )

}

export default App
