import { useState, useEffect } from 'react'
import lodgingService from './services/lodging'
import axios from 'axios'
import logo from './IMG/SF-C_logo.jpg'
import './CSS/Signing.css'
import LoginForm from "./LoginForm"
import { getAuthedUser } from "./services/users"

const App = () => {
  const [loggedIn, setLoggedIn ] = useState(true) //MUISTA VAIHTAA
  const [authedUser, setAuthedUser ] = useState(null)
  const [email, setEmail] = useState(null)
  const [password, setPassword] = useState(null)

  const handleLogin = async () => {
      const user = await getAuthedUser()
      setAuthedUser(user)
  }

  useEffect(() => {

    const fetchData = async () => {
        const user = await getAuthedUser()
        user ? setAuthedUser(user) : setAuthedUser(null)
    }
    fetchData()

    
  }, [])

  useEffect(() => {
      if(authedUser){
          setLoggedIn(true)
      }
  }, [authedUser])

  const handleEmailChange = (event) => {
    setEmail(event.target.value)
  }

  const handlePasswordChange = (event) => {
    setPassword(event.target.value)
  }

  return (
      <div id='root'>
          {!loggedIn && (
              <LoginForm handleLogin = {handleLogin}/>
          )}
          {loggedIn && (
                <div id='wrapper'>
                  <div id='header'>
                    <h1>Le Motel de Puro</h1>
                  </div>
                  <div id='middle'>
                    <div className='formBG'>
                      <h1>TERVETULOA!</h1>
                      <h2>Aloita kirjautumalla</h2>
                      <form>
                        <label>Sähköposti:</label> <br/>
                        <input type='email' onChange={handleEmailChange} value={email}/> <br/>
                        <label>Salasana:</label> <br/>
                        <input type='password' value={password} onChange={handlePasswordChange}/> <br/>
            
                        <button className='submit' type='submit'>KIRJAUDU</button> 
                      </form>
                      <p>
                        <a href=''>Unohdin salasanani</a>
                      </p>
                    </div>
                  </div>
                  <div id='footer'>
                    <p>Heta Puro 2022</p>
                  </div>
                </div>
          )}
      </div>
  )

}

export default App
