import './App.css'

const App = () => {
  const [adults, setAdults] = useState(1)
  const [children, setChildren] = useState(0)
  const [arrival, setArrival] = useState(new Date())

  const addLodging = (event) => {
    event.preventDefault()
    const lodging = {
      arrival,
      adults,
      children,
    }
  }

  const minusA = () => {
    if (adults > 1) {
      setAdults(adults - 1)
    }
  }

  const plusA = () => {
    setAdults(adults + 1)
  }

  const minusC = () => {
    if (children > 0) {
