import { useState } from 'react'

const App = () => {
  const [lodgings, setLodgings] = useState([
    { name: 'Arto Hellas' }
  ]) 
  const [newName, setNewName] = useState('')
  let [adults, setAdults] = useState(0)
  const [children, setChildren] = useState(0)

  function minusA() {
    adults =- 1
    setAdults(adults)
  }

  function plusA() {
    adults =+ 1
    setAdults(adults)
  }

  function minusC() {
    children =- 1
    setAdults(children)
  }

  function plusC() {
    children =+ 1
    setAdults(children)
  }

  return (
    <div>
      <h2>Majoittaudu</h2>
      <form>
        <div>
          <label for='arrival'>Saapumispäivä:</label> 
          <br/>
          <input name='arrival' type='date'/>
          <br/>
        </div>
        <div>
          <button id='minusA' onClick={minusA}>-</button>
          <span id='adults'>{adults}</span>
          <button id='plusA' onClick={plusA}>+</button>
        </div>
        <div>
          <button id='minusC' onClick={minusC}>-</button>
          <span id='children'>{children}</span>
          <button id='plusC' onClick={plusC}>+</button>
        </div>
      </form>
      <h2>Numbers</h2>
      ...
    </div>
  )

}

export default App
