import { useState, useEffect } from 'react'
import axios from 'axios'
import './App.css'
// import Lodging from './components/Lodging'

const Lodging = ({lodging}) => {
  const people = lodging.adults + lodging.children
  const arrival = lodging.arrival.toSting()

  return (
    <tr>
      <th>Etunimi Sukunimi</th>
      <th>{arrival}</th>
      <th>{lodging.departure}</th>
      <th>{people}</th>
    </tr>
  )
}

const App = () => {
  const [lodgings, setLodgings] = useState([])

  useEffect(() => {
    console.log('effect')
    axios
      .get('http://localhost:3001/api/lodgings')
      .then(response => {
        console.log('promise fulfilled')
        setLodgings(response.data)
      })
  }, [])

  return (
    <div id='wrapper'>
      <div id='header'>
        <h1>Le Motel de Puro</h1>
      </div>
      <div id='middle'>
        <table>
          <thead>
            <tr>
              <th>Nimi</th>
              <th>Saapumispäivä</th>
              <th>Lähtöpäivä</th>
              <th>Henkilöt</th>
            </tr>
          </thead>
          <tbody>
            {lodgings.map(lodging =>
              <Lodging key={lodging.id} lodging={lodging} />
              )}
          </tbody>
        </table>
      </div>
      <div id='footer'>
        <p>Heta Puro 2022</p>
      </div>
    </div>
  )

}

export default App
