import { useState } from 'react'
import './App.css'

// const Button

const App = () => {
  const [lodgings, setLodgings] = useState([
    { name: 'Arto Hellas' }
  ]) 
  const [adults, setAdults] = useState(1)
  const [children, setChildren] = useState(0)

  const minusA = () => {
    if (adults > 1) {
      setAdults(adult - 1)
    }
  }

  const plusA = () => {
    setAdults(adult + 1)
  }

  const minusC = () => {
    if (children > 0) {
      setChildren(children - 1)
    }
  }

  const plusC = () => {
    setChildren(children + 1)
  }

  var people = adults + children

  return (
    <div id='wrapper'>
      <div id='header'>
        <h1>Le Motel de Puro</h1>
      </div>
      <h2>Majoittaudu</h2>
      <form>
        <div>
          <label for='arrival'>Saapumispäivä:</label> 
          <br/>
          <input name='arrival' type='date'/>
          <br/>
        </div>
        <div>
          <label for='adults'>Aikuisia:</label> <br/>
          <button name='adults' type='button' id='minusA' onClick={minusA}>-</button>
          <span id='adults'>{adults}</span>
          <button type='button' id='plusA' onClick={plusA}>+</button> <br/>

          <label for='children'>Lapsia: </label> <br/>
          <button name='children' type='button' id='minusC' onClick={minusC}>-</button>
          <span id='children'>{children}</span>
          <button type='button' id='plusC' onClick={plusC}>+</button>
          <p>Henkilöiden määrä: {people}</p>
        </div>
      </form>
      <h2>Majoittautumiset</h2>
    </div>
  )

}

export default App
