import {useState} from "react"

const App = () => {
    const [first_number, Set_first_number] = useState(0)
    const [second_number, Set_second_number] = useState(0)
    const [sum, SetSum] = useState(0)

    const handleNumber = (set_function) => {
        return (
            (event) => {
              console.log(event.target.value)
                set_function(event.target.value === "" ? parseInt(event.target.value) : event.target.value )
            }
        )
    }

    const handleSubmit = (event) => {
        event.preventDefault()
        SetSum(first_number+second_number)
    }

    return (
        <div>
            <p>
                The sum is: {sum}
            </p>
            <form onSubmit={handleSubmit}>
                <input value={first_number} onChange={handleNumber(Set_first_number)}/>
                <input value={second_number} onChange={handleNumber(Set_second_number)}/>
                <button type="submit" value="add" text="lol">add</button>
            </form>
        </div>
    )
}

export default App
